mapper.select:
SELECT r.id , r.reportDate , r.reportType , r.adminId , r.reason , r.isAccepted , r.reportedId , r.reportId , c.reportname , r.crewPostId , r.replyId , r.postId , r.suspension_cycle FROM Report r LEFT JOIN ReportCategory c ON r.categoryId = c.id WHERE 1=1
AND reportType = #{reportType}
AND isAccepted = #{isAccepted}
ORDER BY reportDate DESC
LIMIT #{limit} OFFSET #{offset}
